warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
float3 ret7=ret;
if(frame%3==0)ret.z=.7-ret7.x;
    ret=lerp(ret,ret.zxy,0.2); //channel shiftin'

    //add dither
    float2 d_uv=uv_orig*texsize.xy*texsize_noise_lq.zw+rand_frame.xy;
    ret+=(tex2D(sampler_noise_lq,d_uv).xyz-0.5)/256*2;
if(frame%5==0)ret.x=.7-ret7.y;

    // darken (decay) over time
    ret *= 0.9+0.08*saturate(rad*3);
if(frame%7==0)ret.y=.7-ret7.z;

}

comp_hlsl:
sampler sampler_fw_cells;
shader_body
{
   uv*=0.5;
    ret = tex2D(sampler_main, uv).xyz;
    ret=max(ret,tex2D(sampler_main, uv+float2(0.5,0)).xyz);
    ret=max(ret,tex2D(sampler_main, uv+float2(0,0.5)).xyz);
    ret=max(ret,tex2D(sampler_main, uv+float2(0.5,0.5)).xyz);

    float2 l=lum(ret);
    float sat=length(ret-l);

    ret=lerp(lum(ret),ret,sat*32); //over-saturate
    ret=lerp(ret,tex2D(sampler_fw_cells,ret.xy*.4+.6),.15); //take stripes from image
    ret *= 1.80; //gamma
}
