warp_hlsl:
shader_body
{

    // sample previous frame
    ret.xyz = tex2D( sampler_main, uv ).xyz;

      // take the difference between the crisp and blurred images,
      // then add it back into the image.  Creates spots and stripes over time.
      ret.xyz += (ret - GetBlur3(uv))*0.35;
      ret.xyz -= 0.004;

      // add noise:
      float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
      ret.xyz += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)*0.18;

      // desaturate over time, to keep the globs white
      ret.xyz = lerp(ret.xyz, lum(ret.xyz), 0.2);
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;    

    float3 d = float3(texsize.zw, 0)*1.0;
    float b1 = lum(GetPixel(uv + d.xz));
    float b2 = lum(GetPixel(uv - d.xz));
    float b3 = lum(GetPixel(uv + d.zy));
    float b4 = lum(GetPixel(uv - d.zy));

    //ret = 0;
    ret *= -1; 
    ret += saturate((b1-b2)*64)*1.00*float3(1,0.6,0.1);
    ret += saturate((b3-b4)*64)*1.00*float3(0.2,0.5,0.7);
    ret -= saturate( GetBlur1(uv)*4 - 1 );

    ret *= 1.5;
}
