frame_eqs_eel:
db=bass-lbass;
lbass=bass;
vol=1+bass+mid+treb;

rs=above(abs(db),maxdb);
drs=1-rs;

beatrate=1*rs+beatrate*drs;
avgbeatrate=1*rs+avgbeatrate*drs;
bbtime=1*rs+bbtime*drs;
lbbtime=1*rs+lbbtime*drs;
avgdb=1*rs+avgdb*drs;
fbc=fbc*drs+1*rs;
bc=bc*drs+1*rs;
faketime=1*rs+faketime*drs;
lftime=1*rs+lftime*drs;


avgdb=avgdb*.999+abs(db)*.001;

rawbeatb=above(db*fakebeatb,avgdb*1.3);
beatb=rawbeatb*above(time-lbbtime,.1*avgbeatrate);
maxdb=max(maxdb*.9999,abs(db));

bbtime=time*beatb;

beatrate=beatb*(bbtime-lbbtime)+(1-beatb)*beatrate;
avgbeatrate=beatb*(avgbeatrate*.999+beatrate*.001)+(1-beatb)*avgbeatrate;


lbbtime=time*beatb+(1-beatb)*lbbtime;


faketime=faketime+avgbeatrate*.005;
//faketime=0;
fakebeatb=cos(faketime)*.5+.5;
fb=above(fakebeatb,.9999)*above(faketime-lftime,.5*avgfbr);
fbr=(1-fb)*fbr+fb*(fbr*.99+(faketime-lftime)*.01);
avgfbr=avgbr*(1-fb)+fb*(avgfbr*.99+fbr*.01);

fbc=fbc+fb;
lftime=faketime*fb+(1-fb)*lftime;



bc=bc+beatb;

