warp:
uniform sampler2D sampler_prayerwheel;
 shader_body { 
  vec3 ret_1;
  ret_1 = (texture (sampler_main, uv).xyz + ((texture (sampler_noise_lq, 
    (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)
  ).xyz - 0.5) / 256.0));
  vec4 tmpvar_2;
  tmpvar_2 = texture (sampler_prayerwheel, uv_orig);
  vec2 x_3;
  x_3 = (uv - uv_orig);
  ret_1 = (mix (ret_1, tmpvar_2.xyz, vec3(clamp (
    (1.0 - (abs((
      dot (tmpvar_2.xyz, vec3(0.32, 0.49, 0.29))
     - 
      fract((time * 0.2))
    )) * 33.0))
  , 0.0, 1.0))) * (0.97 + (0.03 * 
    clamp ((sqrt(dot (x_3, x_3)) * 200.0), 0.0, 1.0)
  )));
  vec4 tmpvar_4;
  tmpvar_4.w = 1.0;
  tmpvar_4.xyz = ret_1;
  ret = tmpvar_4.xyz;
 }

warp_hlsl:
#define sampler_pic sampler_prayerwheel
sampler2D sampler_pic;

shader_body
{
    ret = tex2D( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*1;

    // mix in img...
    float3 pic = tex2D(sampler_pic, uv_orig).xyz;
    float lum = lum(pic);
    float use_it = abs(lum - frac(time*0.2));
    use_it = saturate(1 - use_it*33);
    ret = lerp(ret, pic, use_it.xxx);

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    ret *= 0.97 + 0.03*saturate( length(uv - uv_orig)*200 );  // center only
}
