       conwar += tex2D( sampler_pw_main, uv + float2(0,-1)*pixelsize).x;
       conwar += tex2D( sampler_pw_main, uv + float2(1,-1)*pixelsize).x;

       conwar += tex2D( sampler_pw_main, uv + float2(-1,0)*pixelsize).x;
       conwar += tex2D( sampler_pw_main, uv + float2(1,0)*pixelsize).x;

