       conwar += tex2D( sampler_pw_main, uv + float2(0,1)*pixelsize).x;
       conwar += tex2D( sampler_pw_main, uv + float2(1,1)*pixelsize).x;

float1 conwag = tex2D( sampler_pw_main, uv + float2(-1, 1)*pixelsize).z;
       conwag += tex2D( sampler_pw_main, uv + float2(1,-1)*pixelsize).z;
       conwag += tex2D( sampler_pw_main, uv + float2(1,-1)*pixelsize).z;

       conwag += tex2D( sampler_pw_main, uv + float2(-1,0)*pixelsize).z;
       conwag += tex2D( sampler_pw_main, uv + float2(1,0)*pixelsize).z;

       conwag += tex2D( sampler_pw_main, uv + float2(-1,1)*pixelsize).z;
       conwag += tex2D( sampler_pw_main, uv + float2(-1,1)*pixelsize).z;
       conwag += tex2D( sampler_pw_main, uv + float2(0,-1)*pixelsize).z;

float1 conwab = tex2D( sampler_pw_main, uv + float2(1,-1)*pixelsize).y;
       conwab += tex2D( sampler_pw_main, uv + float2(1,1)*pixelsize).y;
       conwab += tex2D( sampler_pw_main, uv + float2(0,-1)*pixelsize).y;

       conwab += tex2D( sampler_pw_main, uv + float2(0,0)*pixelsize).y;
       conwab += tex2D( sampler_pw_main, uv + float2(-1,-1)*pixelsize).y;

       conwab += tex2D( sampler_pw_main, uv + float2(-1,1)*pixelsize).y;
       conwab += tex2D( sampler_pw_main, uv + float2(-1,1)*pixelsize).y;
       conwab += tex2D( sampler_pw_main, uv + float2(1,-1)*pixelsize).y;

float1 p = saturate (tex2D( sampler_pw_main, uv).x);


ret.x = saturate((1-abs(conwar-3)))*((1+roam_sin.z)*.75-p);
ret.y = saturate((1-abs(conwag-3)))*((1+roam_cos.y)*.8-p);
ret.z = saturate((1-abs(conwab-3)))*((1+roam_sin.x)*.86-p);
ret.x += saturate(max(1-abs(conwar-2),(1+roam_cos.z)*.93-abs(conwag-3)))*p;
ret.y += saturate(max(1-abs(conwag-2),(1+roam_sin.y)*1.01-abs(conwab-3)))*p;
ret.z += saturate(max(1-abs(conwab-2),(1+roam_cos.x)*1.10-abs(conwar-3)))*p;

}

