warp_hlsl:
shader_body
{
    // add noise to UV's:
    float2 uv2 = uv;
    uv2.xy += tex2D(sampler_noise_hq, rand_frame.xy + uv*3.3*texsize.xy*texsize_noise_hq.zw)*texsize.zw*2;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

ret-=roam_sin*roam_cos.wzy;
    // darken over time
    ret -= 0.004;
}

comp_hlsl:
shader_body
{
    uv = 0.05 + 0.9*uv;

ret = GetPixel(uv);
    uv += -.05*tan(3*(ret.zz+ret.yx));


    // equivalent of a laplacian: [center*4 - each of the 4 neighbors*1]
    ret = 0;
    ret += tex2D(sampler_main, uv ).xyz*4;
    ret -= GetBlur1(uv)*3;

    // little bit of noise:
    ret *= 1 + 0.12*(tex2D(sampler_noise_lq, rand_frame.xy 
           + uv*texsize.xy*texsize_noise_lq.zw).xxx*2-1);

    // palettize:
    ret = pow(ret, float3(0.5, 0.5, 0.7));
ret-=roam_sin.wzy*roam_cos;

    ret = (ret-0.1)*1.1;

