q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass + mid + treb;


k1 =  is_beat*equal(index,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_slow * p2+ (1-dec_slow)*p1;
rott = p2 * 3.14159265359/4;
