frame_eqs_eel:
dec_med = pow (0.9, 30/fps);
dec_slow = pow (0.9, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .1+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%5;
monitor = index2;
q1 = is_beat;
q3 = bass + mid + treb;
q4 = index2;
k1 =  is_beat*equal(index%2,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott_ = p2 * 3.1416/4;
rott =  rott +  .2*30/fps*q2;
q2 = sin(rott);
ran = bnot(q1)*ran + q1 * (int(rand(50))-24);
