trel = trel + .02*30/fps*tran+q1;
q5 = cos(trel);
q6 = sin(trel);
q7 = -q6;//it makes no sense, but commenting q7/8 fucks it up majorly
q8 = q5;
movex = movex*dec_slow + .1*sin(rott)*30/fps;
