bb = bb + bass*bass*0.85*equal(instance,0);
mm = mm + mid*mid*0.85*equal(instance,0);
tt = tt + treb*treb*0.85*equal(instance,0);

q3 = bb*0.012;
q4 = mm*0.012;
q5 = tt*0.012;

rnd1 = if(equal(instance,0),t1,rnd1);
rnd2 = if(equal(instance,0),t2,rnd2);
rnd3 = if(equal(instance,0),t3,rnd3);
rnd4 = if(equal(instance,0),t4,rnd4);
rnd1 = 4*rnd1*(1-rnd1);
rnd2 = 4*rnd2*(1-rnd2);
rnd3 = 4*rnd3*(1-rnd3);
rnd4 = 4*rnd4*(1-rnd4);
t = .6;
t =  (rnd1+time*t) - int(rnd1+time*t);
t = t + rnd2*0.1;
wh = rnd4*asin(1)*4;
wv = 0.25 + rnd3*0.1;

d = 1.4; // makes the perspective impact
zoom = 1;
l = 1;

// Kardan angles

w1 = q3; // first rotation: clockwise in the desktop pane
w2 = q4; // rotation around vertical axis
w3 = q5; // rotation around horizontal axis


// definition of the 3D shape

i = instance;

my_x = t *(cos(wh)*sin(wv)*l);
my_y = (-0.5 +(t-0.75)*(t-0.75))*cos(wv)*l;
my_z = t*(sin(wh)*sin(wv)*l);


// 3D rotations

x1 = cos(w1)*my_x + sin(w1)*my_y;
y1 = -sin(w1)*my_x + cos(w1)*my_y;
z1 = my_z;

x2 = cos(w2)*x1 + sin(w2)*z1;
z2 = -sin(w2)*x1 + cos(w2)*z1;
y2 = y1;

y3 = cos(w3)*y2 + sin(w3)*z2;
z3 = -sin(w3)*y2 + cos(w3)*z2;
x3 = x2;

// perspective calculation

p = tan(asin(1) + atan2(d+z3,sqrt(x3*x3 + y3*y3)));
d = sqrt(x3*x3 + y3*y3 + (z3+d)*(z3+d));

rad = rad/d;
my_x = zoom*sin(atan2(x3,y3))*p;
my_y = zoom*cos(atan2(x3,y3))*p;

x = 0.5 + my_x;
y = 0.5 + my_y;

x = 0.5 + (x-0.5)/q2;
y = 0.5 + (y-0.5)/q1;

