 tmpvar_3.xyz = tmpvar_2;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 uv_1 = (uv * 2.0);
 vec2 tmpvar_2;
 tmpvar_2 = floor((fract(
   (uv_1 * 0.5)
 ) * 2.0));
 uv_1 = ((fract(uv_1) * (1.0 - tmpvar_2)) + (tmpvar_2 * fract(
   (1.0 - uv_1)
 )));
 vec4 tmpvar_3;
 tmpvar_3 = texture (sampler_main, uv_1);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ((texture (sampler_main, (
   ((hue_shader * 1.2) + q20)
 .xy + 
   fract((_qa.xy + (tmpvar_3.xy * 0.2)))
 )) * tmpvar_3.z) * 3.0).xyz;
 ret = tmpvar_4.xyz;
}

