warp_hlsl:
sampler sampler_smalltiled_electric_nebula;
float4 texsize_smalltiled_electric_nebula;

shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // this creates an endless texture zoom:
    {  // the { } block creates a local, temporary scope for any new variables
     float t = frac(time*0.5);
     float zoom1 = pow(0.5, t);
     float zoom2 = zoom1*2;
     float2 uv1 = (uv-0.5) * zoom1 + 0.5;
     float2 uv2 = (uv-0.5) * zoom2 + 0.5;    
     float3 c  = tex2D(sampler_smalltiled_electric_nebula, 
                  uv1*texsize.xy*texsize_smalltiled_electric_nebula.zw).xyz;
     float3 c2 = tex2D(sampler_smalltiled_electric_nebula, 
                  uv2*texsize.xy*texsize_smalltiled_electric_nebula.zw).xyz;
     c = lerp(c, c2, t);
     c *= saturate(rad*1.3)*1.1; //darken @ center
     ret = lerp(ret, c, 0.15);
    }

    // darken over time
    ret *= 0.80; //or try: ret -= 0.004;

}

