init_eqs_eel:
i=0;  
loop(10000, gmegabuf(i)=0;

i=i+1;);

t=217;

t0=time;

frame_eqs_eel:
q6=aspecty;    t=t+.5/fps;    t1=t1+1/fps;  
c=bnot(frame%2);      

s=10*sin(1.3*t);     

i=0;    x=0;     y=0;   p=0;    p1=pow(10,12);

loop(1024*c,    sa=6.28*i/1024; 

p=p+ cos(sa-10*t)*sin(i*t)- sin(sa+10*t)*cos(i*t);        p1=p1+p;

x=x+sin(p1);      y=y+cos(p1);
d=sqrt(x*x+y*y);
rad=.0001*pow(d,1.5);
rad=if(above(rad,.005),.005,rad);

gmegabuf(3*i)=x;     gmegabuf(3*i+1)=y;   gmegabuf(3*i+2)=rad ;


i=i+1;);

///// clears the screen each n seconds

n=10;
decay=above(time-t0,.2)-equal((5*t1)%(5*n),0);

monitor=s;

q2=.003;   //// zoom
