warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.03);
 ret = tmpvar_1.xyz;
}

comp:
uniform sampler2D sampler_rand00;
 shader_body { 
  vec4 tmpvar_1;
  tmpvar_1 = texture (sampler_main, uv);
  vec4 tmpvar_2;
  tmpvar_2.w = 1.0;
  tmpvar_2.xyz = (((texture (sampler_rand00, 
    (0.4 + (0.6 * tmpvar_1.xy))
  ) * tmpvar_1.z) * 3.0).xyz - (roam_sin.wyx * roam_cos.zwx));
  ret = tmpvar_2.xyz;
 }

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

