frame_eqs_eel:
dec_med = pow (0.8, 30/fps);
dec_slow = pow (0.95, 30/fps);
dec_xlow = pow (0.995, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, .2+avg+peak) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %16;
index2 = (index2 + is_beat*bnot(index))%2;

q20 = avg;
q21 = beat;
q22 = peak;
q23 = index;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att;
q27 = index + 1;

peakx = above(q22,1.5*maxp);
maxp = max(maxp, q22);
maxp = maxp*dec_xlow;

k1 =  is_beat*equal(index%3,0);
p1 =  k1*(p1+1) + (1-k1)*p1;
p2 = dec_med * p2+ (1-dec_med)*p1;
rott = p2 * 3.1416/4;

q1 = cos(rott);
q2 = sin(rott);
q3 = -q2;
q4 = q1;

trig = q24 * bnot(index%4);
dir = dir + trig * q26*(int(rand(100))/100-.5)/2 + peakx;
dir_ = dir_ * dec_slow + dir*(1-dec_slow);
dir__ = dir__ * dec_slow + dir_*(1-dec_slow);

q5 = cos(dir__); 
q6 = sin(dir__);

speed = speed * bnot(trig) + trig*q26*5/fps ;
speed_= speed_* dec_slow + speed*(1-dec_slow);

movx = movx + speed_*q6;
movy = movy + speed_*q5;

//movx = movx + speed;
//movy = movy + speed;


q7 = movx;
q8 = movy;

tilt = dir - dir__;
tilt_ = dec_slow*tilt_ + (1-dec_slow)*tilt;


monitor = maxp;
q9 = cos(tilt_*speed_);
q10 = sin(tilt_*speed_);

q12 = time;

warp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = (uv - 0.5);
 ret_1.x = texture (sampler_main, (tmpvar_2 + 0.5)).x;
 ret_1.y = texture (sampler_main, ((tmpvar_2 * 0.98) + 0.5)).y;
 ret_1.z = texture (sampler_main, ((tmpvar_2 * 0.96) + 0.5)).z;
 ret_1 = (ret_1 - (ret_1 * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

