warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz*.8;
    ret += GetBlur1(uv)*.2;

    // darken (decay) over time and distance from center
    ret *= ret/(1.4*rad); //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;    

    ret *= 0.6;

    // LSB accentuate effect: (LSB = least significant bit)
    float3 d = float3(texsize.zw, 0)*1.0;
    float b1 = lum(GetPixel(uv + d.xz));
    float b2 = lum(GetPixel(uv - d.xz));
    float b3 = lum(GetPixel(uv + d.zy));
    float b4 = lum(GetPixel(uv - d.zy));
    float2 c = float2(b1-b2,b3-b4);
    c = frac(c*20);
    c = saturate((c-0.5)*999)*2-1;
    c = lerp(c, 0, saturate( lum(GetBlur2(uv))*30-17 ));
    ret += 0.104*c.x;
    ret += 0.104*c.y;

    ret = lerp(ret, lum(ret), -1);

}
