warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

ret-=roam_sin.wzy*roam_cos.zxy;
    // mix in code based off of Geiss's
    float pic = tex2D(sampler_noise_hq, uv_orig*aspect.xy*.5+.1*roam_cos+.1*time).xyz;
    float lum = lum(pic);
    float use_it = abs(lum+.25 - (0.5+roam_cos.xy*0.15));
    float LFNoise = tex2D(sampler_noise_hq, pic-(uv.yx*8 + (time*.15)));
    use_it = (1-use_it - (20*LFNoise));
    float tex = lerp(ret, pic, use_it.xxx)*1;

ret=tex;
    // darken (decay) over time
    ret *= 0.95; //or try: ret -= 0.004;
}

