frame_eqs_eel:
vol=(bass+mid+treb_att)*.3333;

redsine=.5+.15*bass*sin(time*1.21);
greensine=.5+.15*mid*sin(time*1.133);
bluesine=.5+.15*treb*sin(time*1.114);

redif=if(above(bass,1.2),redsine,if(above(redif,.95),0,redif*.5));
greenif=if(above(mid,1.2),greensine,if(above(greenif,.95),0,greenif*.5));
blueif=if(above(treb,1.2),bluesine,if(above(blueif,.95),0,blueif*.5));

ob_r=redif;
ob_g=greenif;
ob_b=blueif;

wave_x = wave_x + 0.2*sin(0.32*time);
wave_y = wave_y + 0.2*cos(0.32*time);

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_fc_main, uv).xyz - 0.002);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = mix ((texture (sampler_main, uv).xyz * 50.0), (texture (sampler_main, (
   ((uv - 0.5) * vec2(-1.0, 1.0))
  + 0.5)).xyz * 50.0), vec3(0.5, 0.5, 0.5));
 ret_1 = ((ret_1 * (1.0 - ret_1)) * 12.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // darken (decay) over time
    ret -= 0.002;
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - .5)*1*float2(-1,1) + .5;
    ret = lerp( tex2D(sampler_main, uv).xyz*50, 
                tex2D(sampler_main, uv_echo).xyz*50, 
                .5 
              ); //video echo
    ret *= 1.0; //gamma
    ret = ret*(1-ret)*12; //solarize
    ;
}
