dec_slow = pow (0.95, 30/fps);
//dec_xlow = pow (0.999, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, -.2+avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %32;
index2 = (index2 + is_beat*bnot(index))%4;
index3 = (index2 +index3 + is_beat*bnot(index))%4;

is_beat2 = above (time, t1+.1);
t1 = is_beat*time + (1-is_beat)*t1;
q22 = peak;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att;
vol = vol*dec_med + (1-dec_med)*q26;
q27 = index + 1.5;
q28 = index2 +1;

p2 = .3*sin(time) + .3*cos(time/3);
//*****tunnel tilting
q1 = cos(p2 * 3.1416/4);
q2 = sin(p2 * 3.1416/4);
q3 = -q2;
q4 = q1;

//****tunnel viewpoint x,y
