    ret = tex2D( sampler_main, uv ).xyz;

    // motion blur:
    // 'v' points exactly one pixel, in the direction of motion
    float2 v = normalize(uv-uv_orig)*texsize.zw;
    float3 s;
    ret = max(ret, tex2D(sampler_main, uv+v*-1)*0.90);
