{
    ret = tex2D( sampler_main, (uv-0.5)*1.00 + 0.5 );
    ret /= ret.x+ret.y+ret.z;
    float zoom = dot(ret, float3(1,0.985,0.95));

    ret.xyz = tex2D( sampler_main, (uv-0.5)*zoom + 0.5 );

    // shift mass from red to green and on to blue (corresponds to AGE of pixel)
    // .x = fastest layer, .z = slowest
    float r = 0.02; 
    float3 xfer = saturate((ret - 0.05)*99);
    xfer.yz *= saturate((0.1-ret.xy)*99);
    ret += xfer.xxx*float3(-1,1,0)*r*0.7;
    ret += xfer.yyy*float3(0,-1,1)*r*4;
