warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, (uv + (
   (texture (sampler_main, (uv + texsize.zw)).zx - 0.37)
  * 0.01))).xyz - 0.004);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = mix ((texture (sampler_main, uv).xyz * 50.0), (texture (sampler_main, (
   ((uv - 0.5) * vec2(-1.0, 1.0))
  + 0.5)).xyz * 50.0), vec3(0.5, 0.5, 0.5));
 ret_1 = ((ret_1 * (1.0 - ret_1)) * 12.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.zx-0.37) * 0.01;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - .5)*1*float2(-1,1) + .5;
    ret = lerp( tex2D(sampler_main, uv).xyz*50, 
                tex2D(sampler_main, uv_echo).xyz*50, 
                .5 
              ); //video echo
    ret *= 1.0; //gamma
    ret = ret*(1-ret)*12; //solarize
    ;
}
