float xlat_mutablea;
float xlat_mutableb;
float xlat_mutablec;
 shader_body { 
  vec2 uv_1;
  uv_1 = (uv * 2.0);
  vec2 tmpvar_2;
  tmpvar_2 = floor((fract(
    (uv_1 * 0.5)
  ) * 2.0));
  uv_1 = ((fract(uv_1) * (1.0 - tmpvar_2)) + (tmpvar_2 * fract(
    (1.0 - uv_1)
  )));
  uv_1 = ((0.5 + (
    (uv_1 - 0.5)
   * vec2(0.75, -0.9))) - vec2(0.0, 0.05));
  vec3 tmpvar_3;
  tmpvar_3 = (((
    (((texture (sampler_blur3, uv_1).xyz * scale3) + bias3) + ((texture (sampler_blur2, uv_1).xyz * scale2) + bias2))
   + 
    ((texture (sampler_blur1, uv_1).xyz * scale1) + bias1)
  ) + texture (sampler_main, uv_1).xyz) + 0.04);
  xlat_mutablea = (tmpvar_3.x / 4.0);
  xlat_mutableb = (tmpvar_3.y / 4.0);
  xlat_mutablec = (tmpvar_3.z / 4.0);
  xlat_mutablec = ((xlat_mutablec + xlat_mutableb) - (xlat_mutablec * xlat_mutableb));
  xlat_mutablea = ((xlat_mutablea + xlat_mutablec) - (xlat_mutablea * xlat_mutablec));
  vec3 tmpvar_4;
  tmpvar_4 = vec3((1.4 * xlat_mutablea));
  vec4 tmpvar_5;
