warp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1.x = (1.0 - uv.x);
 tmpvar_1.y = uv.y;
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (max (texture (sampler_main, uv).xyz, texture (sampler_main, tmpvar_1).zyx) * 0.97);
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz - ((texture (sampler_blur1, uv).xyz * scale1) + bias1));
 vec2 tmpvar_2;
 tmpvar_2 = ((0.5 - uv) + 0.5);
 ret_1 = (ret_1 * 4.5);
 ret_1 = (mix (ret_1, (
   (texture (sampler_main, tmpvar_2).xyz - ((texture (sampler_blur1, tmpvar_2).xyz * scale1) + bias1))
  * 4.5), vec3(0.5, 0.5, 0.5)) * 1.5);
 ret_1 = (vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))) * ((hue_shader * 8.0) - 7.25));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    ret = max(ret, tex2D( sampler_main, float2(1-uv.x,uv.y) ).zyx );

    // darken over time
    ret *= 0.97; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 - GetBlur1(uv);
    float2 uv_echo = (uv - 0.5)*float2(-1,-1) + 0.5;
    float3 ret2 = tex2D(sampler_main, uv_echo).xyz - GetBlur1(uv_echo);
    ret *= 4.5;
    ret2 *=4.5;
    ret = lerp(ret,ret2,0.50);
    ret *= 1.5;
    ret = lum(ret);
    ret *= (hue_shader*8)-7.25;

}
