comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - 0.5)*1.000*float2(-1,1) + 0.5;
    float3 one = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma

    float2 uv_echo_two = (uv-0.5)*0.95*(1 + bass*0.025)*float2(-1,1) + 0.5; 
    float3 two = lerp( tex2D(sampler_main, (uv-0.5)*0.9*(1 + bass*0.05) + 0.5 ).xyz, 
                tex2D(sampler_main, uv_echo_two).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma


    ret = one%two*2;     


    ret = ret; //solarize
}
