warp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1.x = (uv.x + (texsize.x * sin(
   (time * 0.00125)
 )));
 tmpvar_1.y = (uv.y + uv.y);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (texture (sampler_main, tmpvar_1).xyz * 0.98);
 ret = tmpvar_2.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz * 2.0);
 ret_1 = (ret_1 * ret_1);
 if (((bass > treb) && (bass > mid))) {
   ret_1.x = (ret_1.x * 1.25);
 };
 if (((mid > treb) && (mid > bass))) {
   ret_1.y = (ret_1.y * 1.25);
 };
 if (((treb > mid) && (treb > bass))) {
   ret_1.z = (ret_1.z * 1.25);
 };
 ret_1 = ((ret_1 * (1.0 - ret_1)) * 4.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

