// Loadus & Geiss custom shape design

ang = q4*(0.13 + 0.1*t1);
rad = rad * (0.5 + 0.2*t2);
r = min(1,max(0,r + 0.2*sin(time*0.417 + 1)));
g = min(1,max(0,g + 0.2*sin(time*0.391 + 2)));
b = min(1,max(0,b + 0.2*sin(time*0.432 + 4)));
