frame_eqs_eel:
q1 = aspectx;
q2 = aspecty;
rot = 0;
zoom = 1;
warp = 0;

vol = bass*8 + mid*5 + treb*3;
m = m*0.96 + vol*0.06;
monitor = vol-m;
beat = above(vol,res)*above(vol,m)*above(vol,10);
diff = (1-beat)*diff + beat*(vol-res);
res = beat*(vol + m*0.15) + (1-beat)*(res -  (0.15+diff*0.03)*60/fps);
res = max(0,res);

r = if(beat, 0.02*(rand(200)-100)*0.01,r);
rot = r;

c_x = if(beat,0.5 + 0.5*(rand(200)-100)*0.01, c_x);
c_y = if(beat,0.5 + 0.5*(rand(200)-100)*0.01, c_y);
v = if(beat,0.035 + 0.025*(rand(200)-100)*0.01, v);


q3 = c_x;
q4 = c_y;
q5 = v;
q6 = beat;

pixel_eqs_eel:
d = (pow(sqrt(sqr(x-q3)+sqr(y-q4)),0.5)-0.);
v = q5;
dx = v*(x-q3)*d;
dy = v*(y-q4)*d;

