is_beat = above(beat, 0.2+avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %12;
index2 = (index2 + is_beat*bnot(index))%8;

q20 = avg;
q21 = beat;
q22 = peak;
q24 = is_beat;
