waves.point_eqs_eel:
n=sample*6.283;

flip=flip+1;
flip=flip*below(flip,2);

phase=sin(n*3)*sin(n*7.9)*sin(n*16.7)*sin(n*63.5)*6;

xp=sin(n+phase)*flip;
yp=cos(n+phase)*flip;
zp=0;

//rotate on y during draw
ang=n*2+phase*0.2;
xq=xp*sin(ang) + zp*cos(ang);
yq=yp;
zq=xp*cos(ang) - zp*sin(ang);

//rotation;
ang=time*0.1;
xp=xq*sin(ang) + zq*cos(ang);
yp=yq;
zp=xq*cos(ang) - zq*sin(ang);

ang=time*0.17;
xq=xp;
yq=yp*sin(ang) + zp*cos(ang);
zq=yp*cos(ang) - zp*sin(ang);


//push into viewspace
zq=zq+3.1;


//project into screenspace
xs=xq/zq;
ys=yq/zq;


x=xs+0.5;
y=ys*1.3+0.5;
a=(1-flip)*0.05;

frame_eqs_eel:
ob_r = 0.5 + 0.4*sin(time*1.324);
ob_g = 0.5 + 0.4*cos(time*1.371);
ob_b = 0.5+0.4*sin(2.332*time);
ib_r = 0.5 + 0.25*sin(time*1.424);
ib_g = 0.25 + 0.25*cos(time*1.871);
ib_b = 1-ob_b;
volume = 0.15*(bass+bass_att+treb+treb_att+mid+mid_att);
xamptarg = if(equal(frame%15,0),min(0.5*volume*bass_att,0.5),xamptarg);
xamp = xamp + 0.5*(xamptarg-xamp);
xdir = if(above(abs(xpos),xamp),-sign(xpos),if(below(abs(xspeed),0.1),2*above(xpos,0)-1,xdir));
xaccel = xdir*xamp - xpos - xspeed*0.055*below(abs(xpos),xamp);
xspeed = xspeed + xdir*xamp - xpos - xspeed*0.055*below(abs(xpos),xamp);
xpos = xpos + 0.001*xspeed;
dx = xpos*0.05;
yamptarg = if(equal(frame%15,0),min(0.3*volume*treb_att,0.5),yamptarg);
yamp = yamp + 0.5*(yamptarg-yamp);
ydir = if(above(abs(ypos),yamp),-sign(ypos),if(below(abs(yspeed),0.1),2*above(ypos,0)-1,ydir));
yaccel = ydir*yamp - ypos - yspeed*0.055*below(abs(ypos),yamp);
yspeed = yspeed + ydir*yamp - ypos - yspeed*0.055*below(abs(ypos),yamp);
ypos = ypos + 0.001*yspeed;
dy = ypos*0.05;
wave_a = 0;
q8 =oldq8+ 0.0003*(pow(1+1.2*bass+0.4*bass_att+0.1*treb+0.1*treb_att+0.1*mid+0.1*mid_att,6)/fps);
oldq8 = q8;
q7 = 0.003*(pow(1+1.2*bass+0.4*bass_att+0.1*treb+0.1*treb_att+0.1*mid+0.1*mid_att,6)/fps);
rot = 0.4 + 1.5*sin(time*0.273) + 0.4*sin(time*0.379+3);

warp:
uniform sampler2D sampler_smalltiled_electric_nebula;
 shader_body { 
  float tmpvar_1;
  tmpvar_1 = fract((time * 0.5));
  float tmpvar_2;
  tmpvar_2 = pow (0.5, tmpvar_1);
  vec2 tmpvar_3;
  tmpvar_3 = (uv - 0.5);
  vec4 tmpvar_4;
  tmpvar_4.w = 1.0;
  tmpvar_4.xyz = (mix (texture (sampler_fc_main, uv).xyz, (
    mix (texture (sampler_smalltiled_electric_nebula, (((
      (tmpvar_3 * tmpvar_2)
     + 0.5) * texsize.xy) * texsize_smalltiled_electric_nebula.zw)).xyz, texture (sampler_smalltiled_electric_nebula, (((
      (tmpvar_3 * (tmpvar_2 * 2.0))
     + 0.5) * texsize.xy) * texsize_smalltiled_electric_nebula.zw)).xyz, vec3(tmpvar_1))
   * 
    (clamp ((rad * 1.3), 0.0, 1.0) * 1.1)
  ), vec3(0.15, 0.15, 0.15)) * 0.8);
  ret = tmpvar_4.xyz;
 }

warp_hlsl:
sampler sampler_smalltiled_electric_nebula;
float4 texsize_smalltiled_electric_nebula;

shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;

    // this creates an endless texture zoom:
    {  // the { } block creates a local, temporary scope for any new variables
     float t = frac(time*0.5);
     float zoom1 = pow(0.5, t);
     float zoom2 = zoom1*2;
     float2 uv1 = (uv-0.5) * zoom1 + 0.5;
     float2 uv2 = (uv-0.5) * zoom2 + 0.5;    
     float3 c  = tex2D(sampler_smalltiled_electric_nebula, 
                  uv1*texsize.xy*texsize_smalltiled_electric_nebula.zw).xyz;
     float3 c2 = tex2D(sampler_smalltiled_electric_nebula, 
                  uv2*texsize.xy*texsize_smalltiled_electric_nebula.zw).xyz;
     c = lerp(c, c2, t);
     c *= saturate(rad*1.3)*1.1; //darken @ center
     ret = lerp(ret, c, 0.15);
    }

    // darken over time
    ret *= 0.80; //or try: ret -= 0.004;

}
