comp_hlsl:
shader_body
{
float2 uv1 = (uv-0.5)*aspect.xy;

float rad2 = length(uv1) + .05;
float rad1 = .1/rad2 ;

float2 uv2 = float2 (ang/3.14, rad1*1.5);
uv2.y = uv2.y  +0.1*time;
uv2.x = uv2.x  +.02*time;

float2 uv3 = float2 (ang/3.14, rad1);
uv3.y = uv3.y  + q30 ;
uv3.x = uv3.x  + time/32;


float3 crisp = GetPixel(frac(uv2)) + 2*GetPixel(frac(uv3));
crisp += 2*GetBlur2(frac(uv2)) + 2*GetBlur2(frac(uv3));

float3 lay1 = float3 (1,0,1)*uv.y*pow(1-rad,8);

crisp = 3*crisp * pow(rad,1);

float mask = saturate(1-4*rad);

ret = crisp + lay1*mask + mask * GetPixel(frac(uv));

ret *= .8;
}
