12*above(ladder,0.4)*below(ladder,0.6) + 18*above(ladder,0.6)*below(ladder,0.8) +
24*above(ladder,0.8);
leafset = if(equal(thresh,2), leaf, leafset);

zoom = zoom + 0.05*(0.75-sin(leafset*5))*(1-rad);
zoom = zoom - abs(0.05*(0.75-cos(4*ang)));

zoom = zoom + tg3*tan(4*q4)*below(zoom,0.22)*above(zoom,-0.22);

zoom = zoom + 0.0095*(sin(10*ang) + sin(sin(time*2*sin(time)*rad))*0.3 - cos(rad)*0.1);
rot = rot + 0.08*abs(0.746-rad)*sin(2.2*(0.5-rad)+5.7*sin(0.1*time));
