init_eqs_eel:
oldx=.5;
oldy=.5;
colorcounter = 1;
counter1 = 0;
counter2 = 1;
cdelay1 = 0;
cdelay2 = 0;

frame_eqs_eel:
warp = 0;
wave_r = min(1,max(0,0.2*bass));
wave_g = min(1,max(0,0.2*mid));
wave_b = min(1,max(0,0.2*treb));
counter1 = if(equal(counter2,1),if(equal(counter1,1),0,counter1+.2),1);
counter2 = if(equal(counter1,1),if(equal(counter2,1),0,counter2+.2),1);
cdelay1 = if(equal(cdelay2,1),1,if(equal(colorcounter%2,1),if(equal(counter1,1),2 ,0), if(equal(counter2,1),2,0)));
cdelay2 = if(equal(cdelay1,2),1,0);
colorcounter = if(above(colorcounter,7),0,if(equal(cdelay1,1),colorcounter+1,colorcounter));
ob_r = if(equal(colorcounter,1),1, if(equal(colorcounter,2),1, if(equal(colorcounter,3),1, if(equal(colorcounter,4),sin(counter2+2.1), if(equal(colorcounter,5),0, if(equal(colorcounter,6),0,sin(counter1)))))));
ob_g = if(equal(colorcounter,1),0, if(equal(colorcounter,2),sin(counter2*.5), if(equal(colorcounter,3),sin((counter1+1.75)*.4), if(equal(colorcounter,4),1, if(equal(colorcounter,5),1, if(equal(colorcounter,6),sin(counter2+2),0))))));
ob_b = if(equal(colorcounter,1),sin(counter1+2.1), if(equal(colorcounter,2),0, if(equal(colorcounter,3),0, if(equal(colorcounter,4),0, if(equal(colorcounter,5),sin(counter1), if(equal(colorcounter,6),1,1))))));
rot = .2*sin(time)+abs(.1*sin(time/2));

xmovn = 0.01*int(rand(6))-0.03;
ymovn = 0.01*int(rand(6))-0.03;
xmov = if(beat,xmovn,xmov);
ymov = if(beat,ymovn,ymov);
q1=oldx;
q2=oldy;
out_x = bor(above(q1+xmov,.8),below(q1+xmov,.2));
out_y = bor(above(q2+ymov,.8),below(q2+ymov,.2));
xmov = xmov + (-2*xmov*out_x);
ymov = ymov + (-2*ymov*out_y);
wave_x =  q1+xmov ;
wave_y = q2+ymov;
q1=wave_x;
q2=wave_y;
oldx = q1;
oldy = q2;

decay = decay - 0.91*(treble);

//Thanks to krash for beat detection (modified)
volume = 0.3*bass+mid;
beatrate = equal(beatrate,0) + (1-equal(beatrate,0))*(below(volume,0.01)+(1-below(volume,0.01))*beatrate);
lastbeat = if(beat,time,lastbeat);
meanbass_att = 0.1*(meanbass_att*9+bass_att);
peakbass_att = max(bass_att,peakbass_att);
beat = above(volume,0.8)*above(bass_att,meanbass_att*1.2)*above(time-lastbeat, 0.1 + 0.5*(beatrate-0.1));
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9+time-lastbeat), beatrate) ,beatrate),0.1);
monitor = beatrate;

pixel_eqs_eel:
mrad = pow(pow(x-q1,2)+pow(y-(1-q2),2),0.5)*pow(2,0.5);
cx = q1;
cy = 1-q2;
rot= rot + below(mrad-0.2,0)*0.5*sin(mrad*40)*(mrad-0.2)*10- above(mrad-0.2,0)*0.5*sin(mrad*2)*(mrad-0.2)*sin(cx*cy*10);
zoom= zoom - above(mrad-0.1,0)*0.05*mrad;
