frame_eqs_eel:
wave_r = 0.5 + 0.5*sin(1.6*time);
wave_g = 0.5 + 0.5*sin(4.1*time);
wave_b = -1 + (1-wave_r + 1-wave_g);
warp = 0;
pfthresh = above(bass_att,pfthresh)*3+(1-above(bass_att,pfthresh))*((pfthresh-1.3)*0.96+1.3);
pfdx_r = equal(pfthresh,3)*0.015*sin(5*time)+(1-equal(pfthresh,3))*pfdx_r;
pfdy_r = equal(pfthresh,3)*0.015*sin(6*time)+(1-equal(pfthresh,3))*pfdy_r;
q1 = wave_r;
q2 = wave_g;
q3 = wave_b;

ob_r = 1-abs(q1)*0.875;
ob_g = 1-abs(q2)*0.875;
ob_b = 1-abs(q3)*0.875;
echo_zoom = echo_zoom + min(max(0.75,50*pfdx_r),1);
echo_orient = echo_orient + 99*pfdy_r;

dx = dx + 12*pfdx_r;
dy = dy + 12*pfdy_r;
ob_size = ob_size + 0.005*bass_att;
ib_a = ib_a - min(0.5,0.5-abs(dx+dy));
monitor = echo_orient;

pixel_eqs_eel:
thresh = above(bass_att,thresh)*2+(1-above(bass_att,thresh))*((thresh-1.3)*0.96+1.3);
dx_r = equal(thresh,2)*0.015*sin(5*time)+(1-equal(thresh,2))*dx_r;
dy_r = equal(thresh,2)*0.015*sin(6*time)+(1-equal(thresh,2))*dy_r;

ladder = abs(sin(1*time));
leaf = 3*below(ladder,0.2) + 4*above(ladder,0.2)*below(ladder,0.4) +
5*above(ladder,0.4)*below(ladder,0.6) + 6*above(ladder,0.6)*below(ladder,0.8) +
7*above(ladder,0.8);
leafset = if(equal(thresh,2), leaf, leafset);

dy = dy + 3*dy_r*(0.5-cos(leafset*ang))*(rad);
dx = dx + 3*dx_r*(0.5-cos(leafset*ang))*(rad);
zoom = zoom + abs(0.05*(0.75-cos(leafset*rad)));

