frame_eqs_eel:
decay=1;
ob_r=1-abs(sin(time/2));
ob_g=1-abs(sin(time/2));
ob_b=1-abs(sin(time/2));
ib_r=1-abs(cos(time/3));
ib_g=1-abs(cos(time/3));
ib_b=1-abs(cos(time/3));
q1 = if(above(abs(ob_size - ib_size),0.06),17,0.8);
ob_size=abs(sin(time/(5*q1))/13);
ib_size=0.08-ob_size;
volume = (bass_att+mid_att+treb_att)/3;
beat_threshold = 1.7/((tb+tt)*0.5);
time_threshold = 0.8;
highpitch = 0.9 * treb_att + 0.1 * mid_att;
lowpitch = bass_att;
beat_bass = if(above(lowpitch,last_bass*beat_threshold),1,0);
beat_treb = if(above(highpitch,last_treb*beat_threshold),1,0);
timegap_bass = if(equal(beat_bass,1), (time-ltb), tgb);
timegap_treb = if(equal(beat_treb,1), (time-ltt), tgt);
tgb = timegap_bass;
tgt = timegap_treb;
last_time_bass = if(equal(beat_bass,1), time, ltb);
last_time_treb = if(equal(beat_treb,1), time, ltt);
ltb = last_time_bass;
ltt = last_time_treb;
tempo_bass = if(equal(beat_bass,1), (tb * 0.92 + 0.08 * tgb), tb);
tempo_treb = if(equal(beat_treb,1), (tt * 0.92 + 0.08 * tgt), tt);
tb = tempo_bass;
tt = tempo_treb;
q2 = beat_bass;
q3 = beat_treb;
q4 = ob_r;
q5 = ib_r;
q6 = volume;









monitor = downbeat;


last_treb = highpitch;
last_bass = lowpitch;
rot=sin(time/5.3);
beat = 0;
dx= sin(time/4.4)/5;
dy= cos(time/3.6)/5;

