waves.frame_eqs_eel:
tic = time - tir;
tir = time;

mixa = (mixa + q1*tic)*below(mixa,10000);
t1 = mixa - int(mixa);
mixb = (mixb + q2*tic)*below(mixb,10000);
t2 = mixb - int(mixb);
mixc = (mixc + q3*tic)*below(mixc,10000);
t3 = mixc - int(mixc);

waves.point_eqs_eel:
it = (it+1)*above(sample,0);

dc = (dc + equal(it%48,0))*above(sample,0);

lx = t1*equal(dc,0) + (1-t3)*(equal(dc,2) + equal(dc,1)) + t2*equal(dc,3);
ly = 0 + (equal(dc,1) + equal(dc,3))*.975;

mx = equal((it+6)%6,0)*-.1 + equal((it+1)%6,0)*-.1 + equal((it+4)%6,0)*.1 + equal((it+3)%6,0)*.1;
my = (my + equal((it+3)%6,0)*.01 + equal((it)%6,0)*.01)*(above(sample,0) - equal((it*.5)%48,0));

x = if(below(dc,2),lx + mx*.2,ly + my*.1);
y = if(below(dc,2),ly + my*.1,lx + mx*.2);

b = 1;
r = equal(mx,0)*.8;
g = equal(mx,0)*1;
a = equal(mx,0)*below(dc,4);

frame_eqs_eel:
decay=1;

warp = 0;

tic = min(time - tin,.1);
tin = time;

ra = 10;

treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);

rb = 1;
vav = tic*(vav*(1/tic - rb) + rb*(bass+treb+mid)*.33333);

q1 = treb_avg;
q2 = mid_avg;
q3 = bass_avg;
q4 = vav;

mt=(mt+tic*vav*100)*below(mt,10000);

q7=sin(mt*0.02)*0.1;
q8=sin(mt*0.01)*0.1;

dx=sin(mt*0.1)*0.07;
dy=cos(mt*0.069)*0.07;

monitor = mt;

