wave_r = 0.5 + 0.5*sin(1.6*time);
wave_g = 0.5 + 0.5*sin(4.1*time);
wave_b = -1 + (1-wave_r + 1-wave_g);
warp = 0;
pfthresh = above(bass_att,pfthresh)*2+(1-above(bass_att,pfthresh))*((pfthresh-1.3)*0.96+1.3);
pfdx_r = equal(pfthresh,2)*0.015*sin(5*time)+(1-equal(pfthresh,2))*pfdx_r;
pfdy_r = equal(pfthresh,2)*0.015*sin(6*time)+(1-equal(pfthresh,2))*pfdy_r;
rg1 = abs(sin(time));
rg2 = 0*above(rg1,0.75) + 3*below(rg1,0.25) + 1*above(rg1,0.25)*below(rg1,0.5)+
2*above(rg1,0.5)*below(rg1,0.75);
rg3 = if(equal(pfthresh,2),rg2,rg3);

q1 = wave_r;
q2 = wave_g;
q3 = wave_b;

cog = ocog+0.005*(bass+bass_att+(bass*bass_att)-2);
ocog = below(cog,30000)*cog;
q4 = cog;

rcog = orcog + 0.5*(rand(100)*0.01);
orcog = below(rcog,20000)*rcog;
q5 = rcog;

pixel_eqs_eel:
thresh = above(bass_att,thresh)*2+(1-above(bass_att,thresh))*((thresh-1.3)*0.96+1.3);
dx_r = equal(thresh,2)*0.015*sin(5*time)+(1-equal(thresh,2))*dx_r;
dy_r = equal(thresh,2)*0.015*sin(6*time)+(1-equal(thresh,2))*dy_r;
tg1 = abs(sin(time));
tg2 = 12*dx_r*above(tg1,0.5) + 12*dy_r*below(tg1,0.5);
tg3 = if(equal(thresh,2),tg2,tg3);


zoom = zoom + abs(12*dx_r*(0.4*sin(6*time)-rad*(3*cos(5*q4))));

warp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * texsize_noise_lq.zw) + rand_frame.xy)).xyz - 0.5)
  / 256.0) * 7.0));
 vec3 tmpvar_2;
 tmpvar_2 = fract((ret_1 - (
   (clamp (((vol / vol_att) - 1.1), 0.0, 1.0) * 0.5)
  + 0.004)));
 ret_1 = tmpvar_2;
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = tmpvar_2;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    ret = tex2D( sampler_main, uv ).xyz;

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*7;

    // darken over time
    //ret -= 0.004;
    //ret *= 0.99;
    //ret *= 0.97 + 0.03*saturate( length(uv - uv_orig)*200 );  // center only
    float dec = saturate(vol/vol_att - 1.1)*0.5 + 0.004;
    ret = frac(ret - dec);
}
