warp:
shader_body { 
 vec3 ret_1;
 float tmpvar_2;
 tmpvar_2 = (rad * rad);
 ret_1 = (texture (sampler_main, uv).xyz + (1.7 + (
   (rad - (tmpvar_2 * (treb * 0.5)))
  * 0.8)));
 ret_1 = (ret_1 - ((1.0 + 
   ((tmpvar_2 * rad) * ((tmpvar_2 * tmpvar_2) * rad))
 ) + (bass * 0.01)));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

