pixel_eqs_eel:
newx = x - q1;
newy = y - q6;
newrad = min(sqrt((newx)*(newx)+0.5625*(newy)*(newy))*2,sqrt(2));
newang = atan2(newx,newy);
newzoom = pow(0.995 + 0.05*sin((q4)*(newang+q3)), pow(sin(newrad*newrad), newrad));
dx = (newx)*newzoom - newx;
dy = (newy)*newzoom - newy;
newx = x - q2;
newy = y - q7;
newrad = min(sqrt((newx)*(newx)+0.5625*(newy)*(newy))*2,sqrt(2));
newang = atan2(newx,newy);
newzoom = pow(0.995 + 0.05*sin((q5)*(newang)), pow(sin(newrad*newrad), newrad));
dx = dx + (newx)*newzoom - newx;
dy = dy + (newy)*newzoom - newy;
newx = x - (0.33*(q1+q2+q6));
newy = y - (0.33*(3-q2-q6-q7));
newrad = min(sqrt((newx)*(newx)+0.5625*(newy)*(newy))*2,sqrt(2));
newang = atan2(newx,newy);
newzoom = pow(1.04 + 0.05*sin(0.5*(q4+q5)*(newang)), pow(sin(newrad*newrad), newrad));
dx = dx + (newx)*newzoom - newx;
dy = dy + (newy)*newzoom - newy;

warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, (uv + (
   (texture (sampler_main, (uv + texsize.zw)).xy - 0.37)
  * 0.03))).xyz - 0.004);
 ret = tmpvar_1.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.03;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;
}
