shapes.frame_eqs_eel:
vol = 1 + 0.2*((bass_att+treb_att+mid_att)/3);
bob = bob*above(bob,0.01) - 0.01 + 1*(1 - above(bob,0.01));
bob = 0.4 + 0.4*sin(time*0.8);
bob = bob*vol;
rad = bob;
border_1 = 0.4;
sides = 30;
ro = ro + 0.02;
ang = ro;
rad = 0.6;

sp = red*0.025;
spi = 0.5 - sp;
tm = time*0.1;
border_r = 0.5 + sp*sin(tm*0.6) + spi*cos(tm*1.46);
border_g = 0.5 + sp*sin(tm*1.294) + spi*cos(tm*0.87);
border_b = 0.5 + sp*sin(tm*1.418) + spi*cos(tm*0.76);

init_eqs_eel:
avgdb=.5;
db=0;
lbass=0;
avgdt=.5;
dt=0;
ltreb=0;

frame_eqs_eel:
db=(bass-lbass)*fps*.1;
lbass=bass;
avgdb=avgdb*.99+abs(db)*.01;

dt=(treb-ltreb)*fps*.06;
ltreb=treb;
avgdt=avgdt*.99+abs(dt)*.01;


pitch=(avgdt-avgdb)*.5+.5;



fc=60/fps;
q6=avgdt;
bb=above(abs(db),avgdb*6);
bt=above(abs(dt),avgdt*6);
t1=t1+bt*treb*fc;
t2=t2+bb*bass*fc;
t3=t3+avgdb*.05*fc;
t4=t4+avgdt*.05*fc;

q5=t3;
q6=t4;
q7=t2;
q8=t1;

decay=1;
warp=0;
wrap=1;

cx=sin(t1)*.5+.5;
cy=sin(t2)*.5+.5;

warp:
shader_body { 
 vec2 uv2_1;
 vec2 tmpvar_2;
 tmpvar_2.x = bass;
 tmpvar_2.y = treb;
 vec2 tmpvar_3;
 tmpvar_3 = ((texture (sampler_main, (
   mix (uv, uv_orig, vec2(-1.0, -1.0))
  + texsize.zw)).xy - 0.37) * (0.005 + (0.045 * 
   clamp ((tmpvar_2 - 1.0), 0.0, 1.0)
 )));
 uv2_1.x = (uv.x + ((tmpvar_3.x * q1) + (tmpvar_3.y * q2)));
 uv2_1.y = (uv.y + ((tmpvar_3.x * q2) - (tmpvar_3.y * q1)));
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = (texture (sampler_main, uv2_1).xyz - 0.004);
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 3.0));
 ret_1 = (ret_1 + ((texture (sampler_blur2, 
   (((uv - 0.5) * 0.333) + 0.5)
 ).xyz * scale2) + bias2));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 t = 0.005 + 0.045*saturate(float2(bass,treb)-1);
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    float2 v = (blurry_color.xy-0.37) * t;
      //uv2 += v;
      // instead, add a rotated version:
      uv2.x += v.x*q1 + v.y*q2;
      uv2.y += v.x*q2 - v.y*q1;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 +
              GetBlur1(uv)*3 +
              GetBlur2(uv)*0 +
              GetBlur3(uv)*0 +
              0;
    ret += GetBlur2( (uv-0.5)*0.333 + 0.5);
    ret *= 1;
}
