sampler sampler_lichen;

shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, 
        lerp(uv,uv_orig,-1) + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.5) * q1;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // push all colors toward 0.5:
    ret.xyz -= (saturate((ret.xyz-0.5)*99)*2-1)*float3(1,1,0)*0.001;

    float2 uv3 = float2(slow_roam_cos.w,slow_roam_sin.z)*0.7 + uv_orig.xy*0.2;
    ret.xyz *= 0.998 - 0.1*saturate(tex2D(sampler_lichen, uv3).y*5-3.7);

    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret.xyz += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*15;
}

