grav = 2;
resist = 5;
bounce = 0.75;
dt = 0.00015*60/fps;

spx2 = (x1+x3-2*x2)*spring;
spy2 = (y1+y3-2*y2)*spring;
spx3 = (x2+x4-2*x3)*spring;
spy3 = (y2+y4-2*y3)*spring;
spx4 = (x3-x4)*spring;
spy4 = (y3-y4)*spring;

vx2 = vx2*(1-resist*dt) + dt*(spx2);
vy2 = vy2*(1-resist*dt) + dt*(spy2-grav);
vx3 = vx3*(1-resist*dt) + dt*(spx3);
vy3 = vy3*(1-resist*dt) + dt*(spy3-grav);
vx4 = vx4*(1-resist*dt) + dt*(spx4);
vy4 = vy4*(1-resist*dt) + dt*(spy4-grav);

