warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
    //ret = lerp(GetBlur1(uv)*1,ret,ret);
    // darken (decay) over time
    if (ret.x+ret.y+ret.z < (0+((mid+bass+treb)/3)*.3)){
       ret.x = ret.x+.6;
       ret.y = ret.y+.6;
       ret.z = ret.z+.6;
       }else{
       ret = (ret - .005)*0.99; //or try: ret -= 0.004;
    }
}

comp_hlsl:
shader_body
{
    float2 uv2, uv3;
    float ang2, c, s;

    uv -= 0.5;
    uv *= aspect.xy;    

    ang2 = 6.28*0.333;
    c = cos(ang2);
    s = sin(ang2);
    uv2.x =  uv.x*c - uv.y*s;
    uv2.y =  uv.x*s + uv.y*c;

    ang2 = 6.28*0.667;
    c = cos(ang2);
    s = sin(ang2);
    uv3.x =  uv.x*c - uv.y*s;
    uv3.y =  uv.x*s + uv.y*c;

    ret = tex2D(sampler_main, uv + 0.5).xyz;
    ret = max(ret, tex2D(sampler_main,uv2 + 0.5).xyz);
    ret = max(ret, tex2D(sampler_main,uv3 + 0.5).xyz);

    ret *= 1.3;
    ret *= hue_shader*4-2;

}
