warp_hlsl:
shader_body
{
  float2 uv2 = lerp(uv_orig, uv, 0.2);

  // diffused version:
  float ss = 0.3;
  float3 blur = 0.25*(
    tex2D(sampler_main, uv2+ss*texsize.zw*float2( 1, 1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2(-1, 1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2( 1,-1)).xyz +
    tex2D(sampler_main, uv2+ss*texsize.zw*float2(-1,-1)).xyz
  );

  // diffusion:  
  ret = tex2D(sampler_main,uv2);//blur;

  float F2 = 0.035;
  float k2 = 0.060;
  float s = 0.025;
  float scale = 9;

  // reaction:
  float3 z = ret;
  float uvv = ret.x*ret.y*ret.y;
  z.x += (-uvv + F2*(1-ret.x) + 0.0007)*scale;
  z.y += (uvv - (F2+k2)*ret.y)*scale;
  //z.x += s*(16-ret.x*ret.y);
  //z.y += s*(ret.x*ret.y - ret.y - ret.z);
  z.z -= 0.02;

  // apply laplacian:
  z.x += (blur - GetPixel(uv2))*0.2*scale;

  ret = z;  
  //ret *= 0.98 + 0.02*saturate( (0.75 - rad )*10 );
  //ret *= 0.99;

  // add noise:
  ret.x += 0.09*(tex2d(sampler_noise_lq, uv*texsize.xy*texsize_noise_lq.zw + rand_frame.xy).xyz*2-1);

  // darken (decay) over time
  //ret = (ret - 0.002)*0.99;
}

comp_hlsl:
// tribute to suksma!

shader_body
{
    float2 uv2;

    float4 c = float4(1,0.4,0.15,0.1);

    uv2 = uv + float2(1,0)*texsize.zw;
    float gx1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(-1,0)*texsize.zw;
    float gx2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    uv2 = uv + float2(0,1)*texsize.zw;
    float gy1 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;
    uv2 = uv + float2(0,-1)*texsize.zw;
    float gy2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    float3 g = normalize(float3(gx1-gx2, gy1-gy2, 0.14));

    ret = 0.5 + 0.5*g;
    ret *= saturate(length(ret.xy-0.5)*5);
    ret = ret.xxy;
    ret += 1.15;
    ret *= lerp(ret,ret*(GetBlur3(uv)-GetBlur1(uv)*treb_att),pow(hue_shader.xzy,bass_att));
    ret *= ret;

