warp_hlsl:
shader_body
{
    float2 center=normalize(uv-uv_orig);
    float2 splatt=tex2D(sampler_main,uv+texsize.zw);

    //painterly on flow
    uv+=.05*(splatt.xy-0.5)*center;
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret *= 0.97; 
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret=abs(ret-GetBlur1(uv))*6;
    ret+=GetBlur1(uv)*1.5; //add extra glow
    ret*=ret; //make it black
    ret+=dot(ret,ret); //dot product by itself :)
}
