frame_eqs_eel:
bba = .5*bba + .5*bass;
btr = .5*btr + .5*treb;
bmi = .5*bmi + .5*mid;
bt = bt + bbass;
tt = tt + btreb;
mt = mt + bmid;

tm = tm + .05*(bt+tt+mt);

warp = 0;
q1 = sin(.25*tm);
q2 = cos(.25*tm);
dx = .00125*q1;
dy = .00125*q2;
cx = .5+.25*q2;
cy = .5+.25*q1;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_fc_main, mix (uv, uv_orig, vec2(0.15, 0.15)));
 ret_1.yz = tmpvar_2.yz;
 vec4 tmpvar_3;
 tmpvar_3 = texture (sampler_main, uv);
 ret_1.x = (tmpvar_2.x + fract(texture (sampler_main, (uv + 
   ((vec2(1.0, 2.0) * texsize.zw) * tmpvar_3.x)
 )).x));
 vec4 tmpvar_4;
 tmpvar_4 = texture (sampler_blur3, uv);
 ret_1.x = (ret_1.x - ((tmpvar_4.xyz * scale3) + bias3).x);
 ret_1.y = (tmpvar_2.y + fract(texture (sampler_main, (uv + 
   ((vec2(2.0, -2.0) * texsize.zw) * tmpvar_3.y)
 )).y));
 ret_1.y = (ret_1.y - ((tmpvar_4.xyz * scale3) + bias3).y);
 ret_1.z = (tmpvar_2.z + fract(texture (sampler_main, (uv + 
   ((vec2(-2.0, -1.0) * texsize.zw) * tmpvar_3.z)
 )).z));
 ret_1.z = (ret_1.z - ((tmpvar_4.xyz * scale3) + bias3).z);
 ret_1 = ((ret_1 * 0.95) - 0.002);
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_1;
 ret = tmpvar_5.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = ((0.5 - uv) + 0.5);
 ret_1 = (abs(mix (
   ((1.5 * texture (sampler_main, uv).xyz) - (1.25 * ((texture (sampler_blur2, uv).xyz * scale2) + bias2)))
 , 
   ((1.5 * texture (sampler_main, tmpvar_2).xyz) - (1.25 * ((texture (sampler_blur2, tmpvar_2).xyz * scale2) + bias2)))
 , vec3(0.5, 0.5, 0.5))) * 1.33);
 ret_1 = (ret_1 * (0.25 + (0.75 * ret_1)));
 ret_1 = (dot (ret_1, vec3(0.32, 0.49, 0.29)) * ((hue_shader * 8.0) - 6.25));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{

   // sample previous frame
ret = tex2D( sampler_fc_main, lerp(uv,uv_orig,.15)).xyz;

ret.x += frac(GetPixel(uv+float2(1,2)*texsize.zw*GetPixel(uv).x).x);
ret.x -= GetBlur3(uv).x;

ret.y += frac(GetPixel(uv+float2(2,-2)*texsize.zw*GetPixel(uv).y).y);
ret.y -= GetBlur3(uv).y;

ret.z += frac(GetPixel(uv+float2(-2,-1)*texsize.zw*GetPixel(uv).z).z);
ret.z -= GetBlur3(uv).z;


    // darken (decay) over time
    ret = (ret*0.95)-.002; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - 0.5)*float2(-1,-1) + 0.5;
    ret = abs(lerp( 1.5*tex2D(sampler_main, uv).xyz-1.25*GetBlur2(uv), 
                1.5*tex2D(sampler_main, uv_echo).xyz-1.25*GetBlur2(uv_echo), 
                .5 
              )); //video echo
    ret *= 1.33; //gamma
    ret *= .25+.75*ret;
    ret = lum(ret)*((hue_shader*8)-6.25); //old hue shader effect
}
