frame_eqs_eel:
bass_clip = above(bass_att,bass_clip)*2 + (1-above(bass_att,bass_clip))*((bass_clip-1.3)*0.96+1.3);
basstime = basstime + (bass_att*0.09)*bass_att*0.25 + time*0.1;

//state for orientation
state_ori = state_ori + equal(bass_clip,2);
//extra state rot and ori will not be linked
state_ori = if(above(state_ori,4),1, state_ori);

echo_orient = state_ori;

//another state machine for shape tracking
state = state + equal(bass_clip,2);
state = if(above(state,4), 0, state);

//add bouns on beat
xs = xs + 0.1*equal(2,bass_clip);
ys = ys + 0.1*equal(2,bass_clip);

//state 0 elipse
xs = xs + (equal(0,state))*atan(bass_time*4)*0.1;
ys = ys + (equal(0,state))*cos(bass_time*4)*0.1;

//state 1 diag
xs = xs + (equal(1,state))*cos(bass_time*4)*0.1;
ys = ys + (equal(1,state))*cos(bass_time*4)*0.1;

//state 2 -circle
xs = xs + (equal(2,state))*cos(bass_time*4)*0.1;
ys = ys + (equal(2,state))*sin(bass_time*4)*0.1;

//state 3 parametric arc
xs = xs + (equal(3,state))*cos(bass_time*5)*0.1;
ys = ys + (equal(3,state))*cos(bass_time*9)*0.1;

//state 4 arc
xs = xs + (equal(4,state))*atan(bass_time*6)*0.1;
ys = ys + (equal(4,state))*cos(bass_time*2)*0.1;

ib_r = ib_r + 0.5*sin(time*0.899);
ib_g = ib_g + 0.5*sin(time*0.687);
ib_b = ib_b + 0.5*sin(time*1.225);

//project and publish
q1 = 0.5 + 0.4*sin(xs);
q2 = 0.5 + 0.4*sin(ys);

q3 = 0.5 + 0.4*sin(xs + 3.14);
q4 = 0.5 + 0.4*sin(ys + 3.14);

q5 = state_rot;
