ang = q1*5;

sides = q3;

x = q1;
y = q2;

r = r + 0.5*sin(time*1.33);
g = g + 0.5*sin(time*1.959);
b = b + 0.5*sin(time*1.360);

r2 = r2 + 0.5*sin(time*1.944);
g2 = g2 + 0.5*sin(time*1.206);
b2 = b2 + 0.5*sin(time*1.407);

border_r = border_r + 0.5*sin(time*1.430);
border_g = border_g + 0.5*sin(time*1.1007);
border_b = border_b + 0.5*sin(time*1.607);

init_eqs_eel:
state_rot = 0;
state_ori = 0;
state_count = 0;
state_shape = 0;
state = 0;
xs = 0;
xy = 0;

frame_eqs_eel:
bass_clip = above(bass_att,bass_clip)*2 + (1-above(bass_att,bass_clip))*((bass_clip-1.3)*0.96+1.3);
basstime = basstime + (bass_att*0.09)*bass_att*0.25 + time*0.1;

//state for rotation
state_rot = state_rot + equal(bass_clip,2)*9.48;
state_rot = if(above(state_rot,9.49),-4.74 , state_rot);

rot = state_rot;

//state for orientation
state_ori = state_ori + equal(bass_clip,2);

//extra state rot and ori will not be linked
state_ori = if(above(state_ori,4),0, state_ori);

echo_orient = state_ori;

//another state for shape tracking
state = state + equal(bass_clip,2);
state = if(above(state,4), -1, state);

//state_count 0 = sin 1 = cos
state_count = state_count + equal(state,-1);
state_count = if(above(state_count,1), 0 , state_count);

//state for shapes
state_shape = state_shape + equal(bass_clip,2);
state_shape = if(equal(state_shape,7),98 , state_shape);
state_shape = if(above(state_shape,98), 0 , state_shappe);

//reset state
state = state + equal(state,-1);

//add bouns on beat
xs = xs + 0.1*equal(2,bass_clip);
ys = ys + 0.1*equal(2,bass_clip);

speed = 0.08;

//state 0 elipse
xs = xs + (equal(0,state))*atan(bass_time*4)*speed;
ys = ys + (equal(0,state))*cos(bass_time*4)*speed;

//state 1 diag
xs = xs + (equal(1,state))*cos(bass_time*4)*speed;
ys = ys + (equal(1,state))*cos(bass_time*4)*speed;

//state 2 -circle
xs = xs + (equal(2,state))*cos(bass_time*4)*speed;
ys = ys + (equal(2,state))*sin(bass_time*4)*speed;

//state 3 parametric arc
xs = xs + (equal(3,state))*cos(bass_time*5)*speed;
ys = ys + (equal(3,state))*cos(bass_time*9)*speed;

//state 4 arc
xs = xs + (equal(4,state))*atan(bass_time*6)*speed;
ys = ys + (equal(4,state))*cos(bass_time*2)*speed;

//project and publish
q1 = 0.5 + equal(state_count,0)*0.45*sin(xs) + equal(state_count,1)*0.45*sin(xs*1.1);
q2 = 0.5 + equal(state_count,0)*0.45*sin(ys) + equal(state_count,1)*0.45*cos(ys);
q3 = state_shape;
