warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (max (texture (sampler_fc_main, uv).xyz, (texture (sampler_fc_main, 
   mix (uv, uv_orig, vec2(1.5, 1.5))
 ).xyz * 0.97)) - 0.02);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz * 1.7);
 ret_1 = (ret_1 * hue_shader);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame, with some trails...
    float trail_decay = 0.97;
    ret = tex2D( sampler_fc_main, uv ).xyz;
    float3 s2 = tex2D( sampler_fc_main, lerp(uv,uv_orig,1.5) ).xyz*trail_decay;
    ret = max(ret, s2);

    // darken over time
    ret -= 0.02;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret *= 1.70; //old gamma effect
    ret *= hue_shader; //old hue shader effect
}
